mapper.select:
select author, browse_num, category_id, description, id, image_url, status, title, updated_time, created_time, url from carbon_article ca where 1=1
and ca.category_id = #{param.categoryId}
and ca.status = #{param.status}
and ca.title like concat('%',concat(#{param.searchKeywords},'%'))

select *
from carbon_methodology_content
where 1=1

        and (
            name like concat('%',concat(#{param.keyword},'%'))
            or content like concat('%',concat(#{param.keyword},'%'))
        )


        and field_code = #{param.fieldCode}


        and industry_code = #{param.industryCode}

order by id desc
