INSERT INTO `carbon_field` VALUES ('03', ' ', '化学过程', 'Chemical Processes', '0030300000', '项目减少、捕获和/或再利用制造业、消费品、化学品和燃料生产中的高效能气体。', 1, '伯克利/国家发改委', NULL, ' ', 1, 1, '2022-05-01 20:12:32', '2022-05-01 20:12:32');
