INSERT INTO `carbon_field` VALUES ('06', ' ', '工业制造业', 'Industrial Manufacturing', '0030600000', '包括工业能效改进、燃料替代、制造工艺改进和工业废物回收。', 1, '伯克利', NULL, ' ', 1, 1, '2022-05-01 20:12:32', '2022-05-01 20:12:32');
