INSERT INTO `carbon_field` VALUES ('08', ' ', '交通运输', 'Transportation', '0030800000', '包括与交通相关的举措，如实施公共交通系统、提高车队效率（如路线和车辆升级）、卡车停靠站电气化、改进燃料运输以及加大电动汽车使用率。', 1, '伯克利', NULL, ' ', 1, 1, '2022-05-01 20:12:32', '2022-05-01 20:12:32');
INSERT INTO `carbon_field` VALUES ('0101', '01', '牧场添加堆肥', 'Compost Addition to Rangelands', '0040100001', '在牧场施用堆肥，直接增加土壤有机碳，通过促进植物生长间接增加土壤有机碳。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0102', '01', '饲料添加剂', 'Feed Additives', '0040100002', '通过饲料添加剂减少肠道发酵排放（牛的甲烷排放）', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0103', '01', '改善灌溉管理', 'Improved Irrigation Management_x000D_', '0040100003', '安装由可再生能源驱动的灌溉设备，否则这些设备将由化石燃料驱动。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0104', '01', '粪肥沼气池', 'Manure Methane Digester', '0040100004', '安装生物消化器，从牲畜粪便中收集甲烷和其他温室气体。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0105', '01', '氮管理', 'Nitrogen Management', '0040100005', '通过减少施肥量和改变施肥时机，提高农田氮肥施用效率。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0106', '01', '水稻减排', 'Rice Emission Reductions', '0040100006', '改变农田管理和洪水灌溉时间，以减少水稻种植产生的甲烷, 二氧化碳和一氧化二氮排放。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0107', '01', '固体废物分离', 'Solid Waste Separation', '0040100007', '从农业废物流中去除挥发性固体，从而避免甲烷排放和厌氧分解造成的腐烂。安装了机械分离系统，从农场粪便泥浆中分离固体，用于加工、回收和场外销售。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0108', '01', '可持续农业', 'Sustainable Agriculture', '0040100008', '通过各种做法增加农业地区的地上和地下碳，包括施肥、将堆肥残留物返回农田、覆盖作物和将树木引入景观。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0109', '01', '可持续放牧', 'Sustainable Grazing', '0040100009', '通过改善放牧区动物轮作，限制退化草原上的动物数量，以及通过重新种植恢复严重退化的草原，增加放牧区的地上和地下碳。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0201', '02', '碳捕获和提高石油采收率', 'Carbon Capture & Enhanced Oil Recovery', '0040200001', '从工业过程中捕获二氧化碳，然后进行压缩、运输和注入，以便在地下永久储存，同时提高采油率。', 0, ' ', NULL, 'ACR的碳捕获和存储协议', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0202', '02', '碳捕获和储存在塑料中', 'Carbon Capture & Storage in Plastic', '0040200002', '从工业过程中捕获二氧化碳和甲烷，以在塑料中永久封存。', 0, ' ', NULL, 'VCS正在开发一种新的方法', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0301', '03', '己二酸生产中N2O的消除', 'N2O Destruction in Adipic Acid Production ', '0040300001', '在高温下通过催化分解，将N2O排放转化为无害的氮和氧。己二酸生产过程中产生的一氧化二氮（制造尼龙的关键成分）被转移到这种销毁技术中。', 0, ' ', NULL, 'ACR的臭氧消耗物质销毁和高GWP泡沫协议', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0302', '03', '硝酸生产中N2O的消除', 'N2O Destruction in Nitric Acid Production', '0040300002', '安装减排措施和催化还原装置，以消除硝酸工厂和己内酰胺生产厂的N2O排放。硝酸（HNO3）和己内酰胺分别是肥料和合成纤维生产的关键成分。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0303', '03', '环氧丙烷生产', 'Propylene Oxide Production', '0040300003', '通过HPPO技术发明的一种新工艺，用过氧化氢合成环氧丙烷（一种全球主要的中间化学品，用于从纺织到航空航天等多个行业制造聚氨酯），降低能耗和废物产生。与传统的环氧丙烷生产相比，温室气体密集型试剂显著减少，工艺能耗降低，从而减少了排放。', 0, ' ', NULL, 'VCS的VM0023', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0304', '03', 'SF6更替', 'SF6 Replacement', '0040300004', '通过将SF6覆盖气体全部/部分替换为替代覆盖气体（也称为保护气体，它使熔融金属免受快速氧化，从而导致有害的燃烧）来避免SF6排放。替代气体包括HFC134a、全氟-2-甲基-3-戊酮和SO2等。其他活动包括SF6排放物的燃烧或热破坏，以及回收SF6的再利用。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0305', '03', '高级制冷剂', ' ', '0040300005', '使用二氧化碳、氨和丙烷等全球变暖潜能值较低的制冷剂。', 0, ' ', NULL, 'ACR的高级冰箱系统协议', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0306', '03', 'HFC制冷剂回收', ' ', '0040300006', '用回收和再利用的制冷剂替代原HFC制冷剂。', 0, ' ', NULL, 'ACR的认证回收HFC制冷剂协议', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0307', '03', '泡沫生产中HFC的替代', ' ', '0040300007', '在泡沫制造过程中用替代性低冲击泡沫制造工艺替代含有HFCs 的发泡剂的使用。', 0, ' ', NULL, '在泡沫制造和使用协议中使用ACR向先进配方发泡剂过渡', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0308', '03', 'HFC23消除', ' ', '0040300008', '捕获并销毁制冷剂制造副产品产生的HFC23。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0309', '03', '臭氧消耗物质的回收与消除', ' ', '0040300009', '从废弃设备（如空调、冰箱和泡沫）中收集和销毁高GWP的消耗臭氧层物质制冷剂', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0310', '03', '制冷剂泄漏检测', ' ', '0040300010', '利用红外实时泄漏检测系统，减少美国商用制冷系统中氢氟碳化合物（HFC）制冷剂的泄漏。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0401', '04', '造林和再造林', 'Afforestation and Reforestation', '0040400001', '在非城市地区种植树木，减少自然再生障碍。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0402', '04', '避免森林转化', 'Avoided Forest Conversion_x000D_', '0040400002', '保护森林，防止土地转为农业或开发等其他用途。避免转换与REDD+类似，REDD+也侧重于减少森林转换，但通常适用于全球南方的项目。我们通过位置来区分两者；避免森林转换项目使用的协议主要侧重于避免全球北方的森林转换。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0403', '04', '避免草场转化', 'Avoided Grassland Conversion', '0040400003', '保护草原和灌木丛，以避免转化为行作物或其他类型的农业生产。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0404', '04', '改善森林管理', 'Improved Forest Management', '0040400004', '采用增加地上和地下碳储量的做法，包括降低木材采伐效率、延长木材采伐轮伐期、指定储量保护地、燃料负荷处理、人工补植和显著灌溉或施肥。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0405', '04', 'REDD+（减少毁林和森林退化造成的排放）', 'REDD+ (Reducing Emissions from Deforestation and Forest Degradation)_x000D_', '0040400005', '减少全球南方的森林砍伐和退化。许多REDD+项目将减少毁林和森林退化、扩大森林、增加现有森林存量的多项活动捆绑在一起，包括改善森林管理、造林/再造林、植被重建、替代生计计划和清洁炉灶。REDD+中的“+”是指除气候缓解外的许多项目共同利益，包括生物多样性保护和生计改善。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0406', '04', 'REDD+管辖权', 'REDD+ Jurisdictional', '0040400006', 'REDD+项目监测和记录整个国家或地方管辖范围内碳储量的变化，重点关注政府政策和计划，解决森林砍伐和保护森林的驱动因素。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0407', '04', '湿地恢复', 'Wetland Restoration', '0040400007', '恢复三角洲和沿海湿地，包括红树林生态系统。恢复可以包括在三角洲地区从大田作物转向水稻栽培，以及创建潮汐湿地。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0501', '05', '生物消化器', 'Biodigesters', '0040500001', '通过家用和社区用生物分解器收集有机物质，如动物粪便，可用于烹饪和其他当地应用，如供暖，和废水可作肥料。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0502', '05', '捆绑能源效率', 'Bundled Energy Efficiency', '0040500002', '在住宅和非工业建筑中安装多种节能技术，包括防风化、照明、制冷、空调、供暖、抽水泵等。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0503', '05', '净水', 'Clean Water', '0040500003', '提供安全饮用水和家庭用水需求，通过净水技术，如水过滤和将净水技术接入主要水框架，从而减少通过煮沸净水对燃烧木柴的需求。社区钻井是一种提供清洁水的形式，它属于自己的类别。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0504', '05', '社区钻井', 'Community Boreholes_x000D_', '0040500004', '建造或修复社区钻井，以获得清洁饮用水。碳效益来自收获和燃烧不可再生的木柴烧开水时避免的排放。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0505', '05', '锅灶', 'Cookstoves', '0040500005', '建造改良的炉灶，以取代或尽量减少使用粪便或木柴做饭。碳效益是通过减少燃烧生物质的排放以及减少森林砍伐来实现的。炉子通常会减少烹饪时的烟雾，从而对健康有额外的好处。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0506', '05', '照明', 'Lighting', '0040500006', '在住宅和其他非工业建筑中安装新的节能照明系统。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0507', '05', '供暖', 'Weatherization', '0040500007', '在住宅和其他非工业建筑中安装供暖设备和隔热材料。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0601', '06', '铝冶炼厂减排', 'Aluminum Smelters Emission Reductions', '0040600001', '通过计算机控制、实施阳极效应缓解和升级熔炼罐的算法自动化，减少铝冶炼制造过程中的PFC排放。避免PFC排放，提高设备效率，减少电力消耗，总体上减少温室气体排放。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0602', '06', '制砖减排', 'Brick Manufacturing Emission Reductions', '0040600002', '使用低碳密度燃料减少制砖过程中的能源消耗，例如从化石燃料过渡到可再生生物质/碳密度较低的化石燃料/不可再生生物质。制造业的改进包括成分改进、窑炉升级、化学设计和可再生能源发电。主要实施绿地或置换。', 0, ' ', NULL, 'SSC的AMS-III.Z. ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0603', '06', '能源效率', 'Energy Efficiency', '0040600003', '提高各种工业过程的能源效率。能源效率的提高从能源转换设备（如锅炉、电机）的机械升级到影响单一生产要素（如炉子、窑）或多步骤生产系列的工艺改进。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0604', '06', '燃料转换', 'Fuel Switching', '0040600004', '主要通过设备改造和更换，从较高温室气体强度的燃料转向较低温室气体强度的非生物质燃料，如从煤炭/石油到天然气。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0605', '06', '气体系统中的泄漏检测和维修', 'Leak Detection & Repair in Gas Systems', '0040600005', '识别和修复天然气生产、加工、输送、储存、分配系统和炼油厂设施中地面工艺设备中的天然气/炼厂气泄漏。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0606', '06', '矿井甲烷捕集', 'Mine Methane Capture ', '0040600006', '捕获、销毁或使用矿山甲烷，否则这些甲烷会从活跃和废弃的煤炭、天然碱、贵金属和贱金属矿山释放到大气中。', 0, ' ', NULL, 'ACR的捕获和销毁甲烷协议', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0607', '06', '天然气发电', 'Natural Gas Electricity Generation ', '0040600007', '建设新的天然气并网发电厂，取代煤炭等温室气体浓度更高的燃料。这些工厂的燃料来源是化石燃料天然气，与通过分解过程获取的可再生天然气截然不同。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0608', '06', '石油回收', 'Oil Recycling', '0040600008', '回收变压器油，并将使用过的润滑油重新精炼至原始质量。与传统的通过焚烧处理废油相关的基线操作避免了排放。', 0, ' ', NULL, 'ACR的二手润滑油再精炼和变压器油回收协议', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0609', '06', '气动改造', 'Pneumatic Retrofit', '0040600009', '安装或升级高排放和低排放气动控制器（用于自动工业气体处理的机械装置，如压力、温度和流量调节），以避免气体系统中的甲烷泄漏。包括泄漏监测和维修，以进一步减少甲烷泄漏。', 0, ' ', NULL, 'ACR的减排测量和监测方法', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0610', '06', '大学校园减排', 'University Campus Emission Reductions', '0040600010', '提高美国大学校园的能源效率和可再生能源部署', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0611', '06', '废气回收', 'Waste Gas Recovery ', '0040600011', '收集和处理水泥厂和热电厂的副产气体，以供再利用，通常用于发电。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0612', '06', '余热回收', 'Waste Heat Recovery', '0040600012', '安装余热回收系统，收集火力发电厂和集中锅炉的副产品热量，为区域住宅、商业和机构建筑提供持续的热量、热水和气候控制。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0701', '07', '生物量', 'Biomass', '0040700001', '从可再生生物质中产生热量、电力（并网或直接使用）和/或沼气，通常利用农业废弃物生物质。这些项目可能涉及生物质燃烧或厌氧消化以产生沼气。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0702', '07', '地热发电', 'Geothermal', '0040700002', '安装地热发电厂。主要在热电厂中使用燃气-蒸汽联合循环涡轮机，热电厂可能以天然气为燃料。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0703', '07', '水力发电', 'Hydropower_x000D_', '0040700003', '安装大型和小型水力发电厂（HEPP）涡轮机，通过定期的大坝流量操作或向多用途水库增加水电来发电。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0704', '07', '可再生能源组合使用', 'RE Bundled (Bundled Renewables)', '0040700004', '在一个项目中涉及/组合多种形式的可再生能源，通常是风能和太阳能的组合。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0705', '07', '太阳能集中式', 'Solar - Centralized', '0040700005', '安装太阳能组件作为发电设备，用于大规模并网发电。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0706', '07', '太阳能分布式', 'Solar - Distributed_x000D_', '0040700006', '为住宅生产和消费安装小型独立太阳能组件。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0707', '07', '太阳灶', 'Solar Cookers', '0040700007', '为未并网的住宅太阳能烹饪系统提供的配电设备。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0708', '07', '太阳能照明', 'Solar Lighting_x000D_', '0040700008', '分配小型光伏板为住宅照明充电，分配太阳能照明模块，以摆脱木柴和煤油灯燃烧等传统的碳排放光源。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0709', '07', '太阳能热水器', 'Solar Water Heaters', '0040700009', '推广太阳能热水器作为燃料型热水器的替代品。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0710', '07', '风能发电', 'Wind', '0040700010', '安装风力涡轮机并网发电，取代传统的化石燃料或天然气燃烧发电。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0801', '08', '电动汽车充电', 'EV Charging', '0040800001', '在大都会区周围集中部署电动汽车充电站，以支持电动汽车的行驶里程/可达性，并鼓励购买电动汽车而不是化石燃料汽车', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0802', '08', '车队效率', 'Fleet Efficiency_x000D_', '0040800002', '对船队基础设施、车辆设计、船舶驳船船体涂层、可达性、燃料来源、路线扩展、沼气（CNG/LNG）驱动船队的替代等进行升级/改进。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0803', '08', '燃料运输', 'Fuel Transport', '0040800003', '建设液体燃料管道以提高燃料运输效率，从而减少通过柴油燃烧卡车运输现有液体燃料产生的温室气体。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0804', '08', '公共交通', 'Mass Transit', '0040800004', '建立大规模快速交通（MRT）系统，取代汽车等碳密集型个人代用品。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0805', '08', '柴油车停车电气化', 'Truck Stop Electrification_x000D_', '0040800005', '通过安装/使用单系统卡车停车电气化（TSE）技术，减少长途卡车柴油发动机空转产生的直接GHG排放，并且必须单独使用并网电力。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0901', '09', '堆肥', 'Composting', '0040900001', '通过堆肥生物质或其他有机材料（固体废物、动物废物或废水）减少甲烷排放，否则这些材料会在传统废物管理系统（固体废物、动物废物或废水）中厌氧腐烂。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0902', '09', '填埋甲烷', 'Landfill Methane', '0040900002', '减少和燃烧垃圾填埋场的甲烷，包括市政、工业和其他固体废物设施。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0903', '09', '废水中甲烷的回收', 'Methane Recovery in Wastewater', '0040900003', '处理废水以捕获和燃烧甲烷，用厌氧消化器处理，和/或在处置前通过干燥对污泥脱水。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('0904', '09', '垃圾焚烧', 'Waste Incineration_x000D_', '0040900004', '在废物管理设施焚烧废物以产生电力或热量。这项活动不同于生物质能可再生能源项目，因为燃烧的废物不仅仅包括有机物质。', 0, ' ', NULL, ' ', 0, 0, NULL, NULL);
INSERT INTO `carbon_field` VALUES ('09', ' ', '废物管理', 'Waste Management', '0030900000', ' ', 0, '国家发改委', 1, ' ', 0, 0, NULL, NULL);
