-- ----------------------------
-- Procedure structure for P_BEy
-- ----------------------------
DROP PROCEDURE IF EXISTS `P_BEy`;
delimiter ;;
CREATE PROCEDURE `P_BEy`(IN in_parameter1 VARCHAR(255))
BEGIN

   DECLARE v_year VARCHAR(64);

   DECLARE v_vehicleModelCode1 VARCHAR(64);

   DECLARE v_vehicleModelCode2 VARCHAR(64);

   DECLARE v_proYearSeq VARCHAR(64);

   DECLARE v_electricVehicleProCharging VARCHAR(64);

   DECLARE v_NCV VARCHAR(64);

   DECLARE v_EFco2 VARCHAR(64);

   DECLARE v_IR VARCHAR(64);

   DECLARE v_BEy VARCHAR(64);

   DECLARE v_factor1 BIGINT;

   DECLARE v_factor2 BIGINT;

   DECLARE v_factor3 BIGINT;

   DECLARE v_factor4 BIGINT;

   DECLARE v_fi VARCHAR(64);

   SET v_year=in_parameter1->>'$.year';

   SET v_vehicleModelCode1=in_parameter1->>'$.vehicleModelCode1';

   SET v_vehicleModelCode2=in_parameter1->>'$.vehicleModelCode2';

   SET v_proYearSeq=in_parameter1->>'$.proYearSeq';

   SET v_electricVehicleProCharging=in_parameter1->>'$.electricVehicleProCharging';

     IF v_year IS NULL OR v_vehicleModelCode1 IS NULL OR v_vehicleModelCode2 IS NULL 

     OR v_proYearSeq IS NULL OR v_electricVehicleProCharging IS NULL THEN

       SELECT '参数值为空' AS CODE;

     ELSE 

       SELECT T1.factor_id

         INTO v_factor1

              FROM (x_carbon_factor_parameter_value T1 

                JOIN x_carbon_factor_parameter_value T2 

                  ON T1.factor_id= T2.factor_id)

                    JOIN x_carbon_factor_parameter_value T3

                      ON T1.factor_id= T3.factor_id

                        WHERE T1.parameter_value =''

                          AND T1.parameter_pos='1'

                            AND T2.parameter_value =''

                              AND T2.parameter_pos='2'

                                AND T3.parameter_value='1060000002';

       SELECT T1.factor_id

           INTO v_factor2

               FROM x_carbon_factor_parameter_value T1

                 JOIN x_carbon_factor_parameter_value T2

                   ON T1.factor_id= T2.factor_id

                     WHERE T1.parameter_value=''

                       AND T1.parameter_pos='1'

                         AND T2.parameter_value=''

                           AND T2.parameter_pos='2'

                             LIMIT 1,1;

       SELECT T1.factor_id

           INTO v_factor3

               FROM x_carbon_factor_parameter_value T1

                 JOIN x_carbon_factor_parameter_value T2

                   ON T1.factor_id= T2.factor_id

                     WHERE T1.parameter_value=v_vehicleModelCode2

                       AND T2.parameter_value=v_proYearSeq

                         AND T1.index_code='8031400007'

                           LIMIT 0,1;

       SELECT T1.factor_id

         INTO v_factor4

              FROM (x_carbon_factor_parameter_value T1 

                JOIN x_carbon_factor_parameter_value T2 

                  ON T1.factor_id= T2.factor_id)

                    JOIN x_carbon_factor_parameter_value T3

                      ON T1.factor_id= T3.factor_id

                        WHERE T1.parameter_value =v_year

                          AND T2.parameter_value =v_vehicleModelCode1

                            AND T3.parameter_value=v_vehicleModelCode2

                             AND T1.index_code='8031400003';

       SELECT factor_value

          INTO v_NCV

            FROM x_carbon_factor 

              WHERE factor_id=v_factor1;

       SELECT factor_value

          INTO v_EFco2

            FROM x_carbon_factor 

              WHERE factor_id=v_factor2;

       SELECT factor_value

          INTO v_IR

            FROM x_carbon_factor

              WHERE factor_id=v_factor3;

       SELECT factor_value

          INTO v_fi

            FROM x_carbon_factor

              WHERE factor_id=v_factor4;

       SET v_BEy=ROUND(v_fi*v_electricVehicleProCharging*v_NCV*v_EFco2*v_IR,0);

     END IF;

     SELECT v_BEy;

END
;;
delimiter ;

